package naru.web;

import java.io.IOException;
import java.util.Map;

import org.apache.log4j.Logger;

import naru.queuelet.Queuelet;
import naru.queuelet.QueueletContext;

/**
 * @author NARU
 *
 * TODO To change the template for this generated type comment go to
 * Window - Preferences - Java - Code Style - Code Templates
 */
public class Response implements Queuelet {
	static private Logger logger=Logger.getLogger(Response.class);
	private QueueletContext context;
	
	/* (non-Javadoc)
	 * @see naru.queuelet.Queuelet#service(java.lang.Object)
	 */
	public boolean service(Object req) {
		HttpContext httpContext=null;
		httpContext=(HttpContext)req;
		httpContext.passQueue(AccessLog.QUEUE_RESPONSE);
		try {
			httpContext.responseDirect();
		} catch (IOException e) {
			logger.error("responseDirect error.",e);
		}finally{
			context.enque(httpContext,Config.QUEUE_CLOSE);
		}
		return true;
	}

	/* (non-Javadoc)
	 * @see naru.queuelet.Queuelet#init(naru.queuelet.QueueletContext, java.util.Map)
	 */
	public void init(QueueletContext context, Map param) {
		this.context=context;
	}

	/* (non-Javadoc)
	 * @see naru.queuelet.Queuelet#term()
	 */
	public void term() {
	}

}
